INSERT INTO `coaches` (`id`, `name`, `photo`, `description`, `section_id`, `work`, `created_at`, `updated_at`, `deleted_at`) VALUES (1,'Веретенников Вадим Витальевич','1556195099-veretennikov.jpg','1979 года рождения. Заместитель директора СДЮШОР, образование высшее (окончил Академию физического воспитания и спорта РБ в 2002 году), работает в СДЮШОР с 1998 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК. Присвоена первая тренерско-преподавательская категория и первая категория судья по спорту.',1,'Да','2019-04-25 09:24:59','2019-04-25 10:22:12',NULL),(2,'Чуешков Сергей Петрович','1556195145-chueshkov.jpg','1975 года рождения. Образование высшее (окончил Академию физического воспитания и спорта РБ в 2002 году), работает в СДЮШОР с 2002 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК. Присвоена первая тренерско-преподавательская категория и первая категория судья по спорту.',1,'Да','2019-04-25 09:25:45','2019-04-25 10:22:39',NULL),(3,'Волчун Вероника Валерьевна','1556195201-volchun.jpg','1992 года рождения. Образование среднее специальное (окончила Гомельское училище олимпийского резерва в 2011 году), на данный момент студентка Гомельского университета им.Ф.Скарины. Работает в СДЮШОР с 2010 года. Присвоена вторая тренерско-преподавательская категория и первая категория судья по спорту.',1,'Да','2019-04-25 09:26:41','2019-04-25 10:23:09',NULL),(4,'Копытов Владимир Николаевич','1556264360-kopytov.jpg','<p>1965 года рождения. Высшее физкультурное образование. Заслуженный мастер спорта Республики Беларусь по греко-римской борьбе, тренер-преподаватель высшей категории. Работает в СДЮШОР с 2002 года.</p>',2,'Да','2019-04-26 04:39:20','2019-04-26 04:39:20',NULL),(5,'Варивода Александр Алексеевич','1556264442-varivoda.jpg','<p>1979 года рождения. Высшее физкультурное образование. Кандидат в мастера спорта по греко-римской борьбе, тренер-преподаватель высшей категории, судья международной категории. Работает в СДЮШОР с 1998 года. В 2014 году прошел курсы повышения квалификации в ИППК БГУФК.</p>',2,'Да','2019-04-26 04:40:42','2019-04-26 04:40:42',NULL),(6,'Клеменцев Павел Александрович','1556264480-klementsev.jpg','<p>1982 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2002 года.</p>',2,'Да','2019-04-26 04:41:21','2019-04-26 04:41:21',NULL),(7,'Клеменцев Дмитрий Владимирович','1556264541-klementsev_dm.jpg','<p>1990 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2010 года. В 2017 году прошел курсы повышения квалификации в ИППК БГУФК. Мастер спорта Республики Беларусь.</p>',2,'Да','2019-04-26 04:42:21','2019-04-26 04:42:21',NULL),(8,'Гогнидзе Гога Гогевич','1556264643-gognidthe.jpg','<p>1992 г. р. Мастер спорта Республики Беларусь по греко-римской борьбе. Образование высшее физкультурное. Окончил &quot;Гомельский ГГУ им. Ф. Скорины&quot; в 2015 году. Работает в СДЮШОР с 2018 года.</p>',2,'Да','2019-04-26 04:44:03','2019-04-26 04:44:03',NULL),(9,'Куприянов Виталий Александрович','1556264945-kupriyanov.jpg','<p>1973 года рождения. Высшее физкультурное образование. Тренер-преподаватель высшей категории по легкой атлетике. Работает в СДЮШОР с 1998 года.</p>',3,'Да','2019-04-26 04:49:05','2019-04-26 04:49:05',NULL),(10,'Католиков Сергей Алексеевич','1556264994-katolikov.jpg','<p>1985 года рождения. Высшее физкультурное образование. Тренер-преподаватель первой категории по легкой атлетике. Работает в СДЮШОР с 2004 года.</p>',3,'Да','2019-04-26 04:49:54','2019-04-26 04:49:54',NULL),(11,'Старовойтова Людмила Викторовна','1556265027-starovoytova.jpg','<p>1974 года рождения. Высшее физкультурное образование. Тренер-преподаватель первой категории по легкой атлетике. Работала в СДЮШОР с 1998 г. по 2016 г.</p>',3,'Нет','2019-04-26 04:50:27','2019-04-26 04:52:00',NULL),(12,'Клеменцева Дарья Евгеньевна','1556265112-klementseva.jpg','<p>1992 года рождения. Высшее физкультурное образование, тренер-преподаватель второй категории по легкой атлетике. Работала&nbsp;в СДЮШОР с 2016 г.&nbsp;по 2018 г.</p>',3,'Нет','2019-04-26 04:51:52','2019-04-26 06:51:20',NULL),(13,'Подлобников Максим Александрович','1558608729-podlobnikov.jpg','<p>1994 года рождения. Мастер спорта по тяжелой атлетике. Среднее специальное физкультурное образование, тренер-преподаватель второй категории. Работает в СДЮШОР с 2014 года.</p>',4,'Нет','2019-05-23 07:52:09','2019-05-23 07:55:30',NULL),(14,'Петрушенко Наталья Ильинична','1558614488-petrishina.jpg','<p>1985 г.р. Тренер-преподаватель &laquo;СДЮШОР ППО ОАО &laquo;Гомельстекло&raquo; по волейболу. Образование высшее &ndash; закончила ГГУ им.Ф.Скарины в 2010 году. Вторая тренерская категория. Работает в СДЮШОР с февраля 2015 года.</p>',6,'Да','2019-05-23 09:28:08','2019-05-23 09:28:08',NULL),(15,'Кравченко Руслан Викторович','1558615277-kravchenko.jpg','<p>1982 года рождения. Мастер спорта РБ по тяжелой атлетике. Среднее специальное физкультурное образование, тренер-преподаватеь высшей категории. Работает в СДЮШОР с 2004 года.</p>',4,'Да','2019-05-23 09:41:17','2019-05-23 09:41:17',NULL),(16,'Блашкевич Дмитрий Владимирович','1558615330-blashkevich.jpg','<p>1974 года рождения. Высшее физкультурное образование. Тренер-преподаватель по футболу первой категории.&nbsp; В 2015 году прошел курсы повышения квалификации в ИППК БГУФК. Работал&nbsp;в СДЮШОР с 1996 года по 2020 год.</p>',5,'Нет','2019-05-23 09:42:10','2020-09-15 06:58:02',NULL);
